--
-- Struktura pogleda `blog_number_of_comments`
--
DROP TABLE IF EXISTS `blog_number_of_comments`;

CREATE VIEW `blog_number_of_comments`  AS  select count(`comments`.`extension_id`) AS `all_comments`,`comments`.`extension_id` AS `extension_id` from `comments` where `comments`.`status` = 1 group by `comments`.`extension_id` ;

