--
-- Struktura pogleda `setting_gdpr_per_lang_view`
--
DROP TABLE IF EXISTS `setting_gdpr_per_lang_view`;

CREATE VIEW `setting_gdpr_per_lang_view`  AS  select `c`.`setting_gdpr_id` AS `setting_gdpr_id`,group_concat(`l`.`language_name` separator ', ') AS `languages` from (`setting_gdpr_per_lang` `c` left join `languages` `l` on(`c`.`language_id` = `l`.`language_id`)) group by `c`.`setting_gdpr_id` ;

-- --------------------------------------------------------

--
-- Struktura pogleda `setting_gdpr_view`
