CREATE VIEW `setting_gdpr_view`  AS  select `g`.`setting_gdpr_id` AS `setting_gdpr_id`,`g`.`active` AS `active`,`g`.`valid_from` AS `valid_from`,`g`.`valid_to` AS `valid_to`,`g`.`created_date` AS `created_date`,`g`.`user_id` AS `user_id`,`gw`.`languages` AS `languages` from (`setting_gdpr` `g` left join `setting_gdpr_per_lang_view` `gw` on(`g`.`setting_gdpr_id` = `gw`.`setting_gdpr_id`)) ;

